import { CHANGE_THEME } from "../actionsConstants/actionConstants";

const initialState = { isDarkThemeEnabled : false }

const themeReducer = (state = initialState, action) => {
   const { type } = action;

   switch(type){
    case CHANGE_THEME: 
    return !state;

    default: 
    return state
   }

}

export default themeReducer
